import auth0 from '../lib/auth0'

const CreateStatus=()=>{
    const getMyLocation= () =>{
    if(navigator.geolocation){
        navigator.geolocation.getCurrentPosition(position)
        console.log(position.coords)
    }
    }
    return (
       <div>
        <h1>CreateStatus</h1>
        
        <label><input type='radio' name='status' value='bem'/>Estou Bem</label>
        <label><input type='radio' name='status' value='gripe'/>Estou com sintomas de gripe/resfriado</label>
        <label><input type='radio' name='status' value='covid'/>Estou com sintomas de covid-19</label>

        <button onClick={getMyLocation}>get My Location</button>
        </div>
        )
}
export default CreateStatus
export async function getServerSideProps({ req, res }) {
    const session = await auth0.getSession(req)
if(session){
    return {
        props: {
            isAuth: true,
            user: session.user,
            
        }
    }
}
return {
    props: {
        user: {
            isAuth: false,
            name: 'Junior'
        }
    }
}
}
